function add_book_to_collection(e){var o="";if(create_new_collection=document.getElementById("create_new_collection_"+e),create_new_collection.checked)collection_title=document.getElementById("collection_title_"+e).value,collection_description=document.getElementById("collection_description_"+e).value,collection_is_public=document.getElementById("is_public_"+e).checked,o="/collections/add_book?job_id="+e+"&title="+escape(collection_title)+"&description="+escape(collection_description)+"&is_public="+collection_is_public;else{var c=document.getElementById("idselect_"+e),l=c.options[c.selectedIndex].value;o="/collections/add_book?job_id="+e+"&col_id="+l}$.ajax({url:o,type:"GET",success:function(o){$("#flash").html(o),$("#collection_modal_"+e).modal("hide")}})}function load_collections(e){$.ajax({url:"/collections/load?job_id="+e,type:"GET",success:function(o){$("#collection_modal_"+e).html(o),$("#collection_modal_"+e).modal("show")}})}