function get_comments(e,t,n){$.ajax({url:e+"&commentable_id="+t+"&commentable_type="+n,type:"GET",success:function(e){$("#reviews").html(e)}})}function load_comments(e,t){$.ajax({url:"/comments/get_comments?commentable_id="+e+"&commentable_type="+t+"&page=1",type:"GET",success:function(e){$("#reviews").html(e)}})}function show_replies(e){$("#replies"+e).toggle()}function show_reply(e){$("#open_reply"+e).toggle()}function open_comment(e){$("#comment"+e).toggle(),$("#abuse"+e).toggle()}function confirmDelete(){var e=confirm("Are you sure you want to delete this comment?");return e}function mark_comment(e){$.ajax({type:"GET",url:"/comments/mark?id="+e,dataType:"script",success:function(t){parseInt(t)>="1"&&($("#comment"+e).toggle(),$("#abuse"+e).toggle())}})}function check_comment_value(e){return""==$("#text_"+e).val()?(alert("Comment can't be empty"),!1):!0}