function save_query(e){$.ajax({url:"/user_search_history/save_query?query="+encodeURIComponent(e),type:"GET",success:function(e){$("#flash").html(e),document.getElementById("save_query").style.display="none"}})}function loadformats(e){div_name="#"+e.toLowerCase()+"_hidden",content=$(div_name).html(),content=content.replace(/\n/g,"<br />"),"mods"==e.toLowerCase()&&(content=content.replace(/\s/g," &nbsp;")),$("#modallabel").html(e),$("#modalcontent").html(content)}function load_sci_name_info(e,n,t){$.ajax({url:"/books/sci_name_info?job_id="+n+"&sci_name="+e,type:"GET",success:function(e){$("#name_tip_"+n+"_"+t).tooltipster({content:e,interactive:!0,fixedWidth:280})}})}